member.desc:
		　ファイル選択ダイアログボックスを開きます。
		
var params = %[
filter : [ "テキストファイル(*.txt)|*.txt", "バイナリファイル(*.bin)|*.bin" ],
filterIndex : 1,
name : "",
initialDir : System.exePath,
title : "ファイルを開く",
save : false,
];
if(Storages.selectFile(params))
System.inform("選択したファイルは : " + params.name);
